function NextClick(){if(document.getElementById("nextButton").innerHTML=="Next")document.getElementById("CreatetionForm").submit();else{var n=window.open("/groups/create?closeAfter=true","MsgWindow","width=1000,height=800");n.document.addEventListener("unload",function(){n.location.href.includes("/groups/create")});n.addEventListener("load",function(){var t=n.document.getElementById("createGroupButton");t.onclick=function(){var t=n.$("form").serialize();n.$.ajax({type:"POST",url:n.$("createGroupForm").attr("action"),data:t,success:function(t){var r=document.getElementById("groupSelector"),u=t.split(";"),i;r.selectedIndex>=0&&(i=document.createElement("option"),i.value=u[0],i.text=u[1],r.add(i));n.close()}})}},!1)}}function GroupSelectorOnChange(){var n=document.getElementById("groupSelector"),t=n.options[n.selectedIndex].value;document.getElementById("nextButton").innerHTML=t=="-1"?"Create":"Next"}$(document).ready(GroupSelectorOnChange);